def aggregate_system_data(cpu_usage, ram_usage, disk_space):
    with open('/tmp/system_monitor_output.txt', 'a') as file:
        file.write(f'CPU Usage: {cpu_usage}%\n')
        file.write(f'RAM Usage: {ram_usage} GB\n')
        for drive, space in disk_space.items():
            file.write(f'Disk Space on {drive}: {space} GB\n')
